pragma solidity ^0.7.6;
pragma abicoder v2;
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";

contract PuggMining is OwnableUpgradeable, PausableUpgradeable{
    using SafeMath for uint256;

    /* --- EVENTS --- */
    event AddTask(uint Id, uint points, uint limit);
    event DelTask(uint Id);
    event DoneTasks(uint[] Ids, address[] addrs);
    event WithdrawPoints(uint amount);
    event WithdrawBaseToken(uint amount);

    struct Task {
        uint        Id;
        string      name;
        uint        points;
        uint8       active;
    }

    struct Contributor {
        uint        points;     //total 
        uint        balance;    //balance
    }

    uint[]                              public  allIds;
    mapping(uint => Task)               public  taskmap;
    mapping(address => Contributor)     public  allcontributor;
    address                             public  tokenAddr;

    function __initialize(address _token) external initializer {
        __Ownable_init();
        __Pausable_init();
        tokenAddr = _token;
    }
    
    function isExist(uint Id) public view returns(bool){
        return taskmap[Id].points != 0;
    }

    function addTask (uint Id, string memory name, uint points, uint limit) public onlyOwner{
        require(isExist(Id) == false, "task Id already exists");
        require(points != 0, "task points must be more than 0");
        Task storage task = taskmap[Id];
        task.Id = Id;
        task.name = name;
        task.points = points;
        task.active = 1;
        allIds.push(Id);
        emit AddTask(Id, points, limit);
    }

    function delTask (uint Id) public onlyOwner {
        require(isExist(Id), "task Id is not exists");
        require(taskmap[Id].active == 1, "channel is already deleted");
        taskmap[Id].active = 0;
        emit DelTask(Id);
    }
    
    function getAllIds () public view returns(uint[] memory) {
        return allIds;
    }

    function doneTasks(uint[] memory Ids, address[] memory addrs, bytes[] memory signatures) public onlyOwner {
        require(Ids.length == addrs.length, "array length is not same");
        for (uint i = 0; i < Ids.length; i++) {
            require(isExist(Ids[i]), "task Id is not exists");
            require(verify(addrs[i], "doneTask(uint256)", Ids[i], signatures[i]), "verify signature failed");
            Task memory task = taskmap[Ids[i]];
            Contributor storage con = allcontributor[addrs[i]];
            con.points = con.points.add(task.points);
            con.balance = con.balance.add(task.points);
        }
        emit DoneTasks(Ids, addrs);
    }

    function setToken (address _token) public onlyOwner {
        tokenAddr = _token;
    }

    function withdrawPoints () public whenNotPaused {
        uint amount = allcontributor[_msgSender()].balance;
        require(amount > 0, "insufficient balance");
        allcontributor[_msgSender()].balance = 0;
        IERC20(tokenAddr).transfer(_msgSender(), amount);
        emit WithdrawPoints(amount);
    }


    function withdrawToken() public onlyOwner {
        uint balance = IERC20(tokenAddr).balanceOf(address(this));
        IERC20(tokenAddr).transfer(_msgSender(), balance);
        emit WithdrawBaseToken(balance);
    }

    function getMessageHash(string memory _message, uint Id) public pure returns (bytes32) {
        return keccak256(abi.encodePacked(_message,Id));
    }

    function getEthSignedMessageHash(bytes32 _messageHash)public pure returns (bytes32) {
        return keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", _messageHash));
    }

    function verify(address _signer, string memory _message, uint Id, bytes memory signature) public pure returns (bool) {
        bytes32 messageHash = getMessageHash(_message, Id);
        bytes32 ethSignedMessageHash = getEthSignedMessageHash(messageHash);
        return recoverSigner(ethSignedMessageHash, signature) == _signer;
    }

    function recoverSigner(bytes32 _ethSignedMessageHash, bytes memory _signature) public pure returns (address) {
        (bytes32 r, bytes32 s, uint8 v) = splitSignature(_signature);
        return ecrecover(_ethSignedMessageHash, v, r, s);
    }

    function splitSignature(bytes memory sig)
        public
        pure
        returns (
            bytes32 r,
            bytes32 s,
            uint8 v
        )
    {
        require(sig.length == 65, "invalid signature length");

        assembly {
            /*
            First 32 bytes stores the length of the signature

            add(sig, 32) = pointer of sig + 32
            effectively, skips first 32 bytes of signature

            mload(p) loads next 32 bytes starting at the memory address p into memory
            */

            // first 32 bytes, after the length prefix
            r := mload(add(sig, 32))
            // second 32 bytes
            s := mload(add(sig, 64))
            // final byte (first byte of the next 32 bytes)
            v := byte(0, mload(add(sig, 96)))
        }

        // implicitly return (r, s, v)
    }
}

